import React, { createContext, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "./AuthContext";

export const RideContext = createContext();

export const RideProvider = ({ children }) => {
  const { user } = useContext(AuthContext);
  const [currentRide, setCurrentRide] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const requestRide = async (pickupLocation, destination, estimatedPrice) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/rides`,
        {
          pickupLocation,
          destination,
          estimatedPrice,
        },
        config
      );

      setCurrentRide(res.data);
      setError(null);
      return true;
    } catch (err) {
      setError(err.response?.data?.message || "Failed to request ride");
      return false;
    } finally {
      setLoading(false);
    }
  };

  const cancelRide = async (rideId) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/rides/${rideId}/cancel`,
        {},
        config
      );

      setCurrentRide(null);
      setError(null);
      return true;
    } catch (err) {
      setError(err.response?.data?.message || "Failed to cancel ride");
      return false;
    } finally {
      setLoading(false);
    }
  };

  const completeRide = async (rideId, rating, feedback) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/rides/${rideId}/complete`,
        { rating, feedback },
        config
      );

      setCurrentRide(null);
      setError(null);
      return true;
    } catch (err) {
      setError(err.response?.data?.message || "Failed to complete ride");
      return false;
    } finally {
      setLoading(false);
    }
  };

  const getRideHistory = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/rides/history`,
        config
      );

      setError(null);
      return res.data;
    } catch (err) {
      setError(err.response?.data?.message || "Failed to fetch ride history");
      return [];
    } finally {
      setLoading(false);
    }
  };

  return (
    <RideContext.Provider
      value={{
        currentRide,
        loading,
        error,
        requestRide,
        cancelRide,
        completeRide,
        getRideHistory,
      }}
    >
      {children}
    </RideContext.Provider>
  );
};
