import React, { useState } from "react";
import styled from "styled-components";
import Map from "./Map";

const RideBookingContainer = styled.div`
  display: flex;
  height: calc(100vh - 64px);
  position: relative;
`;

const BookingPanel = styled.div`
  width: 400px;
  background: white;
  padding: 2rem;
  box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
  z-index: 2;
  position: relative;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.8rem;
  margin: 0.5rem 0;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 1rem;
`;

const Button = styled.button`
  width: 100%;
  padding: 1rem;
  background-color: #000;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  cursor: pointer;
  margin-top: 1rem;

  &:hover {
    background-color: #333;
  }
`;

const PriceEstimate = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f5f5f5;
  border-radius: 4px;
`;

function RideBooking() {
  const [bookingData, setBookingData] = useState({
    pickup: "",
    destination: "",
  });
  const [priceEstimate, setPriceEstimate] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate price calculation
    const randomPrice = (Math.random() * 30 + 10).toFixed(2);
    setPriceEstimate(randomPrice);
    console.log("Booking attempt:", bookingData);
  };

  const handleChange = (e) => {
    setBookingData({
      ...bookingData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <RideBookingContainer>
      <Map />
      <BookingPanel>
        <h2>Book a Ride</h2>
        <form onSubmit={handleSubmit}>
          <Input
            type="text"
            name="pickup"
            placeholder="Pickup Location"
            value={bookingData.pickup}
            onChange={handleChange}
            required
          />
          <Input
            type="text"
            name="destination"
            placeholder="Destination"
            value={bookingData.destination}
            onChange={handleChange}
            required
          />
          <Button type="submit">Get Estimate</Button>
          {priceEstimate && (
            <PriceEstimate>
              <h3>Estimated Price</h3>
              <p>${priceEstimate}</p>
              <Button onClick={() => console.log("Booking confirmed!")}>
                Confirm Booking
              </Button>
            </PriceEstimate>
          )}
        </form>
      </BookingPanel>
    </RideBookingContainer>
  );
}

export default RideBooking;
