import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Map from "./Map";
import VehicleSelection from "./VehicleSelection";
import SearchBox from "./SearchBox";
import { useRide } from "../../hooks/useRide";
import axios from "axios";

const RideBookingContainer = styled.div`
  display: flex;
  height: calc(100vh - 64px);
  position: relative;
`;

const MapContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const BookingPanel = styled.div`
  width: 400px;
  height: 100%;
  background: white;
  padding: 2rem;
  box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
  z-index: 2;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.8rem;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 1rem;
`;

const Button = styled.button`
  width: 100%;
  padding: 1rem;
  background-color: ${(props) => (props.disabled ? "#ccc" : "#000")};
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};

  &:hover {
    background-color: ${(props) => (props.disabled ? "#ccc" : "#333")};
  }
`;

const PriceEstimate = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f5f5f5;
  border-radius: 4px;
`;

const LoadingOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.7);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: white;
  z-index: 1000;
`;

function RideBooking() {
  const navigate = useNavigate();
  const [bookingData, setBookingData] = useState({
    pickup: "",
    destination: "",
  });
  const [priceEstimate, setPriceEstimate] = useState(null);
  const [isMatching, setIsMatching] = useState(false);
  const [vehicles, setVehicles] = useState([]);
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [fareEstimate, setFareEstimate] = useState(null);
  const { requestRide, loading: rideLoading, error: rideError } = useRide();
  const [pickupCoords, setPickupCoords] = useState(null);
  const [destinationCoords, setDestinationCoords] = useState(null);

  useEffect(() => {
    // Fetch available vehicle types
    const fetchVehicles = async () => {
      try {
        console.log("Fetching vehicle types...");
        console.log(
          "API URL:",
          `${process.env.REACT_APP_API_URL}/api/vehicles/types`
        );
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/vehicles/types`
        );
        console.log("Received vehicles:", response.data);
        setVehicles(response.data);
      } catch (error) {
        console.error("Error fetching vehicle types:", error.response || error);
        console.error("Full error:", error);
        alert("Error loading vehicle types. Please refresh the page.");
      }
    };
    fetchVehicles();
  }, []);

  useEffect(() => {
    // Calculate fare estimate when pickup, destination, and vehicle are selected
    const calculateFare = async () => {
      if (!bookingData.pickup || !bookingData.destination || !selectedVehicle)
        return;

      console.log("Calculating fare for:", {
        pickup: bookingData.pickup,
        destination: bookingData.destination,
        vehicleType: selectedVehicle,
      });

      try {
        // Mock distance and duration calculation
        const distance = 5;
        const duration = 15;

        const response = await axios.post(
          `${process.env.REACT_APP_API_URL}/api/vehicles/estimate`,
          {
            vehicleType: selectedVehicle,
            distance,
            duration,
          }
        );
        console.log("Fare estimate:", response.data);
        setFareEstimate(response.data);
      } catch (error) {
        console.error("Error calculating fare:", error);
        alert("Error calculating fare. Please try again.");
      }
    };
    calculateFare();
  }, [bookingData.pickup, bookingData.destination, selectedVehicle]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const randomPrice = (Math.random() * 30 + 10).toFixed(2);
    setPriceEstimate(randomPrice);
  };

  const handleChange = (e) => {
    setBookingData({
      ...bookingData,
      [e.target.name]: e.target.value,
    });
  };

  const handleConfirmBooking = () => {
    setIsMatching(true);
    setTimeout(() => {
      setIsMatching(false);
      navigate("/tracking");
    }, 3000);
  };

  const handleBookRide = async () => {
    // Validate inputs
    if (
      !bookingData.pickup ||
      !bookingData.destination ||
      !selectedVehicle ||
      !fareEstimate
    ) {
      alert("Please fill in all required fields");
      return;
    }

    setIsMatching(true);
    try {
      console.log("Booking ride with data:", {
        pickup: bookingData.pickup,
        destination: bookingData.destination,
        vehicleType: selectedVehicle,
        price: fareEstimate.estimatedFare,
      });

      const success = await requestRide({
        pickup: bookingData.pickup,
        destination: bookingData.destination,
        vehicleType: selectedVehicle,
        price: fareEstimate.estimatedFare,
        status: "pending",
        paymentMethod: "cash", // Default payment method
        paymentStatus: "pending",
      });

      console.log("Booking result:", success);

      if (success) {
        setTimeout(() => {
          setIsMatching(false);
          navigate("/tracking");
        }, 2000);
      } else {
        setIsMatching(false);
        alert(rideError || "Failed to book ride. Please try again.");
      }
    } catch (error) {
      console.error("Error booking ride:", error);
      setIsMatching(false);
      alert(
        error.response?.data?.message || "Error booking ride. Please try again."
      );
    }
  };

  const handlePickupSelect = (coords, address) => {
    setPickupCoords(coords);
    setBookingData((prev) => ({
      ...prev,
      pickup: `${coords[0].toFixed(4)}, ${coords[1].toFixed(4)}`,
    }));
  };

  const handleDestinationSelect = (coords, address) => {
    setDestinationCoords(coords);
    setBookingData((prev) => ({
      ...prev,
      destination: `${coords[0].toFixed(4)}, ${coords[1].toFixed(4)}`,
    }));
  };

  const handlePickupChange = (value) => {
    setBookingData((prev) => ({
      ...prev,
      pickup: value,
    }));
  };

  const handleDestinationChange = (value) => {
    setBookingData((prev) => ({
      ...prev,
      destination: value,
    }));
  };

  // Clear any error when component unmounts or when new request is made
  useEffect(() => {
    return () => {
      setIsMatching(false);
    };
  }, []);

  // Show error message if ride request fails
  useEffect(() => {
    if (rideError) {
      alert(rideError);
    }
  }, [rideError]);

  return (
    <RideBookingContainer>
      <MapContainer>
        <Map
          pickup={pickupCoords}
          destination={destinationCoords}
          onPickupSelect={handlePickupSelect}
          onDestinationSelect={handleDestinationSelect}
        />
      </MapContainer>
      <BookingPanel>
        <h2>Book a Ride</h2>
        <Form onSubmit={handleSubmit}>
          <SearchBox
            placeholder="Enter pickup location"
            value={bookingData.pickup}
            onChange={handlePickupChange}
          />
          <SearchBox
            placeholder="Enter destination"
            value={bookingData.destination}
            onChange={handleDestinationChange}
          />
          {bookingData.pickup && bookingData.destination && (
            <VehicleSelection
              vehicles={vehicles}
              selectedType={selectedVehicle}
              onSelect={setSelectedVehicle}
              fareEstimate={fareEstimate}
            />
          )}
          <Button
            type="button"
            disabled={
              !bookingData.pickup ||
              !bookingData.destination ||
              !selectedVehicle ||
              rideLoading
            }
            onClick={handleBookRide}
          >
            {rideLoading
              ? "Booking..."
              : fareEstimate
              ? `Book Ride - â‚¹${fareEstimate.estimatedFare}`
              : "Book Ride"}
          </Button>
        </Form>
      </BookingPanel>
      {(isMatching || rideLoading) && (
        <LoadingOverlay>
          <h2>Finding your driver...</h2>
          <p>This usually takes 1-3 minutes</p>
        </LoadingOverlay>
      )}
    </RideBookingContainer>
  );
}

export default RideBooking;
