import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Map from "./Map";
import VehicleSelection from "./VehicleSelection";
import { useRide } from "../../hooks/useRide";
import axios from "axios";

const RideBookingContainer = styled.div`
  display: flex;
  height: calc(100vh - 64px);
  position: relative;
`;

const BookingPanel = styled.div`
  width: 400px;
  background: white;
  padding: 2rem;
  box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
  z-index: 2;
  position: relative;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.8rem;
  margin: 0.5rem 0;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 1rem;
`;

const Button = styled.button`
  width: 100%;
  padding: 1rem;
  background-color: #000;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  cursor: pointer;
  margin-top: 1rem;

  &:hover {
    background-color: #333;
  }
`;

const PriceEstimate = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f5f5f5;
  border-radius: 4px;
`;

const LoadingOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.7);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: white;
  z-index: 1000;
`;

function RideBooking() {
  const navigate = useNavigate();
  const [bookingData, setBookingData] = useState({
    pickup: "",
    destination: "",
  });
  const [priceEstimate, setPriceEstimate] = useState(null);
  const [isMatching, setIsMatching] = useState(false);
  const [vehicles, setVehicles] = useState([]);
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [fareEstimate, setFareEstimate] = useState(null);
  const { requestRide } = useRide();

  useEffect(() => {
    // Fetch available vehicle types
    const fetchVehicles = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/vehicles/types`
        );
        setVehicles(response.data);
      } catch (error) {
        console.error("Error fetching vehicle types:", error);
      }
    };
    fetchVehicles();
  }, []);

  useEffect(() => {
    // Calculate fare estimate when pickup, destination, and vehicle are selected
    const calculateFare = async () => {
      if (!bookingData.pickup || !bookingData.destination || !selectedVehicle)
        return;

      try {
        // Mock distance and duration calculation
        // In a real app, you would use a mapping service API to calculate these
        const distance = 5; // 5 kilometers
        const duration = 15; // 15 minutes

        const response = await axios.post(
          `${process.env.REACT_APP_API_URL}/api/vehicles/estimate`,
          {
            vehicleType: selectedVehicle,
            distance,
            duration,
          }
        );
        setFareEstimate(response.data);
      } catch (error) {
        console.error("Error calculating fare:", error);
      }
    };
    calculateFare();
  }, [bookingData.pickup, bookingData.destination, selectedVehicle]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const randomPrice = (Math.random() * 30 + 10).toFixed(2);
    setPriceEstimate(randomPrice);
  };

  const handleChange = (e) => {
    setBookingData({
      ...bookingData,
      [e.target.name]: e.target.value,
    });
  };

  const handleConfirmBooking = () => {
    setIsMatching(true);
    setTimeout(() => {
      setIsMatching(false);
      navigate("/tracking");
    }, 3000);
  };

  const handleBookRide = async () => {
    if (
      !bookingData.pickup ||
      !bookingData.destination ||
      !selectedVehicle ||
      !fareEstimate
    )
      return;

    try {
      const success = await requestRide({
        pickup: bookingData.pickup,
        destination: bookingData.destination,
        vehicleType: selectedVehicle,
        price: fareEstimate.estimatedFare,
      });

      if (success) {
        // Navigate to tracking page
      }
    } catch (error) {
      console.error("Error booking ride:", error);
    }
  };

  return (
    <RideBookingContainer>
      <Map />
      <BookingPanel>
        <h2>Book a Ride</h2>
        <form onSubmit={handleSubmit}>
          <Input
            type="text"
            name="pickup"
            placeholder="Pickup Location"
            value={bookingData.pickup}
            onChange={handleChange}
            required
          />
          <Input
            type="text"
            name="destination"
            placeholder="Destination"
            value={bookingData.destination}
            onChange={handleChange}
            required
          />
          <Button type="submit">Get Estimate</Button>
          {priceEstimate && (
            <PriceEstimate>
              <h3>Estimated Price</h3>
              <p>${priceEstimate}</p>
              <Button onClick={handleConfirmBooking}>Confirm Booking</Button>
            </PriceEstimate>
          )}
        </form>
      </BookingPanel>
      {isMatching && (
        <LoadingOverlay>
          <h2>Finding your driver...</h2>
          <p>This usually takes 1-3 minutes</p>
        </LoadingOverlay>
      )}
      {bookingData.pickup && bookingData.destination && (
        <VehicleSelection
          vehicles={vehicles}
          selectedType={selectedVehicle}
          onSelect={setSelectedVehicle}
          fareEstimate={fareEstimate}
        />
      )}
      <Button
        disabled={
          !bookingData.pickup || !bookingData.destination || !selectedVehicle
        }
        onClick={handleBookRide}
      >
        Book Ride
      </Button>
    </RideBookingContainer>
  );
}

export default RideBooking;
